import React, {useState} from 'react';
import axios from 'axios'
import './index.css';


function App() {
  const [data,setData]= useState({})
  const [location,setLocation] =useState('')

  const url =`https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=da8c1040837628e95a1ddbad0d86a369`

    const searchLocation = (event)=>{
      if(event.key === 'Enter'){
      axios.get(url).then((response)=>{
        setData(response.data)
        console.log(response.data)

      })
      setLocation('')
    }
  }

  return (
    <div className="App">
      <div class="search">
        <input value={location} onChange={event=>setLocation(event.target.value)} onKeyPress={searchLocation}  placeholder="please enter the city" type="text"/>
      </div>
      <div className="container">
      <div className="top">
        <div className="location">
          <p>{data.name}</p>
        </div>
        <div className="temp">
          {data.main? <h1>{data.main.temp}F</h1> : null}

        </div>
        <div className="description">
        {data.main? <p1>{data.weather[0].main}F</p1> : null}

        </div>
      </div>

      </div>
      <div className="bottom">
        <div className="feels">
          {/* <p className='bold'>90F</p> */}
          <p>Feels Like</p>
          {data.main ? <p className="bold">{data.main.feels_like}F</p> : null}

        </div>
        <div className="humidity">
          <p className='bold'>Humidity</p>
          {data.main ? <p className="bold">{data.main.humidity}%</p> : null}

        </div>
        <div className="wind">


          {/* <p className='bold'>12 MPH</p> */}
          <p>Wind Speed</p>
          {data.wind ? <p className="bold">{data.wind.speed}MPH</p> : null}
        </div>
      </div>
    </div>
  );
}

export default App;
